CREATE VIEW [dbo].[Contafamiglie]
AS
SELECT        f.descrizione
FROM            dbo.Individui AS i INNER JOIN
                         dbo.Accessioni AS a ON i.accessione = a.id INNER JOIN
                         dbo.Specie AS sp ON a.specie = sp.id INNER JOIN
                         dbo.Generi AS g ON g.id = sp.genere INNER JOIN
                         dbo.Famiglie AS f ON g.famiglia = f.id INNER JOIN
                         dbo.Settori AS sr ON i.settore = sr.id
WHERE        (LTRIM(LOWER(sr.settore)) NOT IN ('nursery', 'banca semi')) AND (i.id IN
                             (SELECT        s.individuo
                               FROM            dbo.StoricoIndividuo AS s INNER JOIN
                                                         dbo.StatoIndividuo AS st ON s.statoIndividuo = st.id
                               WHERE        (s.dataInserimento IN
                                                             (SELECT        MAX(dataInserimento) AS Expr1
                                                               FROM            dbo.StoricoIndividuo AS s
                                                               WHERE        (LTRIM(LOWER(st.stato)) = 'vivo') OR
                                                                                         (LTRIM(LOWER(st.stato)) = 'incerto') OR
                                                                                         (LTRIM(LOWER(st.stato)) = 'malato')
                                                               GROUP BY individuo))))
GROUP BY f.descrizione
GO
